function getRandomDamage(t,a){return Math.floor(Math.random()*(a-t))+t}const app=Vue.createApp({data:()=>({playerHealth:100,monsterHealth:100,round:0,winner:null,battleLog:[]}),watch:{playerHealth(t){t<=0&&this.monsterHealth<=0?this.winner="draw":t<=0&&(this.winner="you lost!")},monsterHealth(t){t<=0&&this.playerHealth<=0?this.winner="draw":t<=0&&(this.winner="you won!")}},computed:{monsterBar(){return{width:this.monsterHealth+"%"}},playerBar(){return{width:this.playerHealth+"%"}}},methods:{attackMonster(){this.round++;let t=getRandomDamage(5,12);this.monsterHealth=this.monsterHealth-t,this.attackPlayer(),this.addLog("player","attack",t)},attackPlayer(){let t=getRandomDamage(8,15);this.playerHealth=this.playerHealth-t,this.addLog("monster","attack",t)},specialAttack(){this.round++;let t=getRandomDamage(10,25);this.monsterHealth=this.monsterHealth-t,this.attackPlayer(),this.addLog("player","special attack",t)},healPlayer(){this.round++;let t=getRandomDamage(8,20);this.playerHealth+t<100?this.playerHealth+=t:this.playerHealth=100,this.attackPlayer(),this.addLog("player","heal",t)},surrender(){this.playerHealth=0},addLog(t,a,e){this.battleLog.unshift({by:t,type:a,value:e})}}});app.mount("#game");