<template><ul><li><h2>{{ name}}{{ isFavourite===true ? 'Favourite' : ''}}</h2><button @click="toggleDetails()">Show Details</button><button @click="toggleFavourite()">Toggle Favourite</button><ul v-if="detailsVisible"><li><strong>Phone:</strong>{{ phoneNumber}}</li><li><strong>Email:</strong>{{ emailAdress}}</li></ul><button @click="deleteFriend">Delete friend</button></li></ul></template><script>export default{ props:{ id:{ type: String}, name:{ type: String, required: true}, phoneNumber:{ type: String, default: 'empty'}, emailAdress: String, isFavourite: Boolean}, emits:{ 'toggle-favourite': function (id){ if (id){ return true;} else{ console.log('id is missing'); return false;}}, 'delete':{}}, // props: [ // 'name', // 'phoneNumber', // 'emailAdress' // ], data(){ return{ detailsVisible: true, // favouriteStatus: this.isFavourite};}, methods:{ toggleDetails(){ this.detailsVisible=!this.detailsVisible;}, // toggleFavourite(){ // this.favouriteStatus=!this.favouriteStatus; //} toggleFavourite(){ this.$emit('toggle-favourite', this.id);}, deleteFriend(){ this.$emit('delete', this.id);}}}</script>