<template><base-card><base-button @click="setSelectedTab('StoredResources')" :mode="selectedTab==='StoredResources' ? null : 'flat'" >Stored Resources </base-button><base-button @click="setSelectedTab('AddResource')" :mode="selectedTab==='StoredResources' ? 'flat' : null" >Add Resources</base-button></base-card><keep-alive><component :is="selectedTab"></component></keep-alive></template><script>import StoredResources from '../learning-resources/StoredResources.vue';import AddResource from '../learning-resources/AddResource.vue';export default{ components:{ StoredResources, AddResource}, data(){ return{ selectedTab: 'StoredResources', storedResources: [ { id: 'official-guide', title: 'official-guide', description: 'The official vue.js documentation', link: 'vuejs.org'}, { id: 'google', title: 'google', description: 'Learn with google', link: 'google.com'} ]};}, provide(){ return{ resources: this.storedResources, addResource: this.addResource, deleteItem: this.deleteItem}}, methods:{ setSelectedTab(tab){ this.selectedTab=tab;}, addResource(title, desc, link){ const newResource={ id: new Date().toISOString(), title: title, description: desc, link: link}; this.storedResources.unshift(newResource); this.selectedTab='StoredResources';}, deleteItem(id){ const resIndex=this.storedResources.findIndex(res=>res.id===id); this.storedResources.splice(resIndex, 1);}}}</script>